import React, { useState, useEffect } from 'react';
import './style/advertise.css';

const Advertise = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const banners = [
    {
      id: 1,
      title: "BIG SALE",
      subtitle: "SHOP NOW",
      price: "PKR",
      product: "Enviro Microwave Oven",
      image: "/banners/microwave.jpg",
      bgColor: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)"
    },
    {
      id: 2,
      title: "SUMMER DEAL",
      subtitle: "SHOP NOW",
      price: "PKR",
      product: "Enviro Air Fryer",
      image: "/banners/airfryer.jpg",
      bgColor: "linear-gradient(135deg, #f093fb 0%, #f5576c 100%)"
    },
    {
      id: 3,
      title: "WINTER SALE",
      subtitle: "SHOP NOW",
      price: "PKR",
      product: "Heating Appliances",
      image: "/banners/heater.jpg",
      bgColor: "linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)"
    },
    {
      id: 4,
      title: "CLEARANCE",
      subtitle: "SHOP NOW",
      price: "PKR",
      product: "Home Essentials",
      image: "/banners/home.jpg",
      bgColor: "linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)"
    },
    {
      id: 5,
      title: "NEW ARRIVALS",
      subtitle: "SHOP NOW",
      price: "PKR",
      product: "Latest Gadgets",
      image: "/banners/gadgets.jpg",
      bgColor: "linear-gradient(135deg, #fa709a 0%, #fee140 100%)"
    }
  ];

  // Auto slide every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000);

    return () => clearInterval(interval);
  }, [currentSlide]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === banners.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? banners.length - 1 : prev - 1));
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <div className="banner-carousel">
      <div className="carousel-container">
        {/* Navigation Buttons */}
        <button className="nav-button prev-button" onClick={prevSlide}>
          <i className="fas fa-chevron-left"></i>
        </button>
        
        <button className="nav-button next-button" onClick={nextSlide}>
          <i className="fas fa-chevron-right"></i>
        </button>

        {/* Banner Slides */}
        <div className="slides-container">
          {banners.map((banner, index) => (
            <div
              key={banner.id}
              className={`slide ${index === currentSlide ? 'active' : ''} ${
                index === currentSlide - 1 || (currentSlide === 0 && index === banners.length - 1)
                  ? 'prev'
                  : ''
              } ${
                index === currentSlide + 1 || (currentSlide === banners.length - 1 && index === 0)
                  ? 'next'
                  : ''
              }`}
              style={{ background: banner.bgColor }}
            >
              <div className="banner-content">
                <div className="banner-text">
                  <h2 className="banner-title">{banner.title}</h2>
                  <p className="banner-subtitle">{banner.subtitle}</p>
                  <div className="price-section">
                    <span className="price">{banner.price}</span>
                    <h3 className="product-name">{banner.product}</h3>
                  </div>
                  <button className="shop-now-btn">
                    Shop Now <i className="fas fa-arrow-right"></i>
                  </button>
                </div>
                <div className="banner-image">
                  <div className="image-placeholder">
                    <i className="fas fa-gift"></i>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Indicators */}
        <div className="indicators">
          {banners.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Advertise;
