import React, { useEffect, useRef, useState } from 'react';
import { Link, Outlet, useLocation, useNavigate } from 'react-router-dom';
import logo from './asserts/Alkaram_logo.jpg'; // replace with your logo path
import './layout.css'; // create this CSS file to style the component

// --- Categories data (main + 5-7 subcategories each) ---
const CATEGORIES = [
  {
    name: 'Beauty & Personal Care',
    icon: 'fas fa-spa',
    sub: [
      'Skincare',
      'Makeup',
      'Hair Care',
      'Fragrances',
      'Oral Care',
    ],
  },
  {
    name: 'Baby Products',
    icon: 'fas fa-baby',
    sub: [
      'Diapers & Wipes',
      'Baby Food',
      'Feeding & Nursing',
      'Bath & Care',
      'Baby Toys',
    ],
  },
  {
    name: 'Cleaning & Households',
    icon: 'fas fa-broom',
    sub: [
      'Floor Care',
      'Laundry',
      'Kitchen Cleaners',
      'Surface Cleaners',
      'Trash & Storage',
    ],
  },
  {
    name: 'Electronic Appliances',
    icon: 'fas fa-tv',
    sub: [
      'Small Appliances',
      'Kitchen Appliances',
      'Personal Care Devices',
      'Chargers & Cables',
      'Batteries',
    ],
  },
  {
    name: 'Accessories (Mobiles)',
    icon: 'fas fa-mobile-alt',
    sub: [
      'Cases & Covers',
      'Chargers & Cables',
      'Power Banks',
      'Headphones',
      'Screen Protectors',
    ],
  },
  {
    name: 'Decor',
    icon: 'fas fa-couch',
    sub: [
      'Wall Art',
      'Vases & Ornaments',
      'Candles & Holders',
      'Rugs',
      'Photo Frames',
    ],
  },
  {
    name: 'Stationary',
    icon: 'fas fa-pen',
    sub: [
      'Notebooks',
      'Writing Instruments',
      'Office Supplies',
      'Art Supplies',
      'Planners',
    ],
  },
  {
    name: 'Crockery',
    icon: 'fas fa-utensils',
    sub: [
      'Dinner Sets',
      'Cups & Mugs',
      'Cookware',
