// --- Layout Component ---
export default function Layout() {
  const [showSidebar, setShowSidebar] = useState(false);
  const [activeCategory, setActiveCategory] = useState(null); // category name hovered
  const [searchQuery, setSearchQuery] = useState('');
  const [searchCategory, setSearchCategory] = useState('All Categories');
  const [cartCount, setCartCount] = useState(2); // demo initial value; wire to context as needed
  const [wishlistCount, setWishlistCount] = useState(1); // demo initial value
  const [showMapModal, setShowMapModal] = useState(false);

  const navigate = useNavigate();
  const location = useLocation();
  const sidebarRef = useRef(null);

  // Close sidebar when clicking outside or pressing Escape
  useEffect(() => {
    function handleClickOutside(e) {
      if (sidebarRef.current && !sidebarRef.current.contains(e.target)) {
        setShowSidebar(false);
      }
    }
    function handleEsc(e) {
      if (e.key === 'Escape') setShowSidebar(false);
    }
    if (showSidebar) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEsc);
    }
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEsc);
    };
  }, [showSidebar]);

  // Handlers
  const toggleSidebar = () => setShowSidebar((s) => !s);

  const handleCategoryClick = (category) => {
    // navigate to products page by category (adjust route as your app expects)
    navigate(`/products?category=${encodeURIComponent(category)}`);
    setShowSidebar(false);
  };

  const handleSubcategoryClick = (main, sub) => {
    navigate(`/products?category=${encodeURIComponent(main)}&subcategory=${encodeURIComponent(sub)}`);
    setShowSidebar(false);
  };

  const handleSearch = (e) => {
    e?.preventDefault();
    const path = `/search?query=${encodeURIComponent(searchQuery || '')}&category=${encodeURIComponent(
      searchCategory === 'All Categories' ? '' : searchCategory
    )}`;
    navigate(path);
  };

  const openMap = () => setShowMapModal(true);
  const closeMap = () => setShowMapModal(false);

  // Example functions to mutate counters (in a real app these would be driven by context / API)
  const demoAddToCart = () => setCartCount((c) => c + 1);
  const demoAddToWishlist = () => setWishlistCount((w) => w + 1);

  return (
    <div className="ak-layout">
      {/* ===== UPPER HEADER ===== */}
      <header className="ak-header ak-upper">
        <div className="ak-brand">
          <Link to="/" className="ak-brand-link" aria-label="Al-Karam home">
            <img src={logo} alt="Al-Karam" className="ak-logo" />
            <div className="ak-brand-text">
              <span className="ak-name">Al-Karam</span>
              <span className="ak-sub">Departmental Store</span>
            </div>
          </Link>
        </div>

        <form className="ak-search" onSubmit={handleSearch} role="search">
          <select
            className="ak-search-cat"
            value={searchCategory}
            onChange={(e) => setSearchCategory(e.target.value)}
            aria-label="Select category"
          >
            <option>All Categories</option>
            {CATEGORIES.map((c) => (
              <option key={c.name} value={c.name}>
                {c.name}
              </option>
            ))}
          </select>

          <input
            className="ak-search-input"
            type="search"
            placeholder="Search for products"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            aria-label="Search products"
          />

          <button className="ak-search-btn" aria-label="Search">
            <i className="fas fa-search" />
          </button>
        </form>

        <div className="ak-actions">
          <button className="ak-locate" onClick={openMap} aria-label="Locate us on map">
            <i className="fas fa-map-marker-alt" />
            <span>Locate us</span>
          </button>

          <div className="ak-account" title="Account">
            <i className="fas fa-user-circle" />
            <div className="ak-account-text">Account</div>
            <div className="ak-account-dropdown">
              <Link to="/login">Login</Link>
              <Link to="/register">Register</Link>
            </div>
          </div>

          <button className="ak-wishlist" onClick={demoAddToWishlist} aria-label="Wishlist">
            <i className="fas fa-heart" />
            {wishlistCount > 0 && <span className="ak-badge">{wishlistCount}</span>}
          </button>

          <button className="ak-cart" onClick={demoAddToCart} aria-label="Cart">
            <i className="fas fa-shopping-cart" />
            {cartCount > 0 && <span className="ak-badge">{cartCount}</span>}
          </button>
        </div>
      </header>

      {/* ===== LOWER HEADER (NAV) ===== */}
      <nav className="ak-header ak-lower" aria-label="Main navigation">
        <div className="ak-left">
          <button className="ak-all-cats" onClick={toggleSidebar} aria-expanded={showSidebar} aria-controls="ak-sidebar">
            <i className="fas fa-bars" />
            <span>All Categories</span>
          </button>
        </div>

        <div className="ak-center">
          <Link className="ak-nav-link" to="/deals">
            Today's Deals
          </Link>
          <Link className="ak-nav-link" to="/category/Accessories%20(Mobiles)">
            Accessories
          </Link>
          <Link className="ak-nav-link" to="/category/Cleaning%20%26%20Households">
            Cleaning &amp; Households
          </Link>
          <Link className="ak-nav-link" to="/category/Personal%20Care">
            Personal Care
          </Link>
          <Link className="ak-nav-link" to="/category/Stationary">
            Stationary
          </Link>
          <Link className="ak-nav-link" to="/category/Electronic%20Appliances">
            Electronic Appliances
          </Link>
        </div>

        <div className="ak-right">
          {/* optional right side (language / offers) */}
          <div className="ak-flag">PK</div>
        </div>
      </nav>

      {/* ===== SIDEBAR (CATEGORIES) ===== */}
      {showSidebar && (
        <div className="ak-sidebar-overlay">
          <aside className="ak-sidebar" id="ak-sidebar" ref={sidebarRef} role="dialog" aria-modal="true">
            <div className="ak-sidebar-header">
              <strong>All Categories</strong>
              <button className="ak-close" onClick={() => setShowSidebar(false)} aria-label="Close sidebar">
                ×
              </button>
            </div>

            <div className="ak-sidebar-body">
              <ul className="ak-cat-list">
                {CATEGORIES.map((c) => (
                  <li
                    key={c.name}
                    className={`ak-cat-item ${activeCategory === c.name ? 'hovered' : ''}`}
                    onMouseEnter={() => setActiveCategory(c.name)}
                    onMouseLeave={() => setActiveCategory(null)}
                    onClick={() => handleCategoryClick(c.name)}
                  >
                    <i className={c.icon} aria-hidden />
                    <span>{c.name}</span>
                    <i className="fas fa-chevron-right ak-cat-chevron" />
                  </li>
                ))}
              </ul>

              {/* Subcategory floating panel */}
              <div className={`ak-subpanel ${activeCategory ? 'visible' : ''}`}>
                {activeCategory ? (
                  <>
                    <h4>{activeCategory}</h4>
                    <ul>
                      {(CATEGORIES.find((c) => c.name === activeCategory)?.sub || []).map((sub) => (
                        <li key={sub} onClick={() => handleSubcategoryClick(activeCategory, sub)}>
                          {sub}
                        </li>
                      ))}
                    </ul>
                  </>
                ) : (
                  <div className="ak-subpanel-prompt">Hover a category to see subcategories</div>
                )}
              </div>
            </div>

            <div className="ak-sidebar-footer">
              <small>Shop smart — curated categories for faster browsing</small>
            </div>
          </aside>
        </div>
      )}

      {/* ===== MAP MODAL ===== */}
      {showMapModal && (
        <div className="ak-modal-overlay" onClick={closeMap}>
          <div className="ak-modal" onClick={(e) => e.stopPropagation()}>
            <button className="ak-modal-close" onClick={closeMap} aria-label="Close map">
              ×
            </button>
            <div className="ak-modal-body">
              {/* Replace the query below with your store coordinates or Google Maps embed URL */}
              <iframe
                title="Al-Karam Location"
                src="https://maps.google.com/maps?q=lahore&z=13&output=embed"
                style={{ border: 0, width: '100%', height: '350px' }}
                loading="lazy"
              />
            </div>
          </div>
        </div>
      )}

      {/* ===== MAIN / ROUTES PLACEHOLDER ===== */}
      <main className="ak-main">
        <Outlet />
      </main>

      {/* ===== NOTES =====
        - This component uses local state for demo counters (cartCount, wishlistCount). Connect to your app's context/store for real values.
        - Add Font Awesome in your index.html: <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/css/all.min.css" />
        - Create Layout.css and style classes prefixed with `ak-` (I used concise class names in this file.)
        - The component exports default Layout and uses react-router's navigate & Link for navigation.
      ===== */}
    </div>
  );
}
