import React, { useState, useEffect } from 'react';
import './style/advertise.css';

const Advertise = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Using simple placeholder images
  const banners = [
    {
      id: 1,
      image: 'https://via.placeholder.com/1200x400/FF0000/FFFFFF?text=Banner+1'
    },
    {
      id: 2,
      image: 'https://via.placeholder.com/1200x400/00FF00/FFFFFF?text=Banner+2'
    },
    {
      id: 3,
      image: 'https://via.placeholder.com/1200x400/0000FF/FFFFFF?text=Banner+3'
    }
  ];

  // Auto slide every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev === banners.length - 1 ? 0 : prev + 1));
    }, 3000);

    return () => clearInterval(interval);
  }, [currentSlide]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === banners.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? banners.length - 1 : prev - 1));
  };

  console.log('Advertise component rendering, current slide:', currentSlide);

  return (
    <div className="banner-carousel" style={{ border: '2px solid blue', padding: '20px' }}>
      <h3>Advertise Component</h3>
      <div className="carousel-container">
        {/* Navigation Buttons */}
        <button className="nav-button prev-button" onClick={prevSlide}>
          ←
        </button>
        
        <button className="nav-button next-button" onClick={nextSlide}>
          →
        </button>

        {/* Banner Slides */}
        <div className="slides-container">
          {banners.map((banner, index) => (
            <div
              key={banner.id}
              className={`slide ${index === currentSlide ? 'active' : ''}`}
              style={{ 
                display: index === currentSlide ? 'block' : 'none',
                height: '300px',
                background: '#f0f0f0'
              }}
            >
              <img 
                src={banner.image} 
                alt={`Banner ${banner.id}`}
                style={{ width: '100%', height: '100%', objectFit: 'cover' }}
              />
            </div>
          ))}
        </div>

        {/* Indicators */}
        <div className="indicators">
          {banners.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => setCurrentSlide(index)}
              style={{
                width: '12px',
                height: '12px',
                borderRadius: '50%',
                border: 'none',
                backgroundColor: index === currentSlide ? '#007bff' : '#ccc',
                margin: '0 5px',
                cursor: 'pointer'
              }}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Advertise;
