// App.js
import React, { useState } from 'react';
import './App.css';
import SignUp from './components/signup';
import Login from './components/login';
import ForgotPassword from './components/ForgotPassword';

function App() {
  const [activeComponent, setActiveComponent] = useState('login');
  const [isLoading, setIsLoading] = useState(false);

  const handleAuthAction = async (action) => {
    setIsLoading(true);
    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 1500));
    setIsLoading(false);
    return action();
  };

  const renderComponent = () => {
    switch(activeComponent) {
      case 'signup':
        return (
          <SignUp 
            switchToLogin={() => setActiveComponent('login')} 
            isLoading={isLoading}
            handleAuthAction={handleAuthAction}
          />
        );
      case 'forgot':
        return (
          <ForgotPassword 
            switchToLogin={() => setActiveComponent('login')} 
            isLoading={isLoading}
            handleAuthAction={handleAuthAction}
          />
        );
      default:
        return (
          <Login 
            switchToSignup={() => setActiveComponent('signup')} 
            switchToForgot={() => setActiveComponent('forgot')} 
            isLoading={isLoading}
            handleAuthAction={handleAuthAction}
          />
        );
    }
  };

  return (
    <div className="App">
      <div className="background-animation">
        <div className="shape shape-1"></div>
        <div className="shape shape-2"></div>
        <div className="shape shape-3"></div>
      </div>

      <header className="app-header">
        <div className="logo-container">
          <div className="logo">AK</div>
          <h1>AL-KARAM Departmental Store</h1>
        </div>
        <p>Premium shopping experience since 1995</p>
      </header>

      <div className="auth-container">
        {renderComponent()}
      </div>

      <footer className="app-footer">
        <p>&copy; 2023 AL-KARAM Departmental Store. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
