// components/ForgotPassword.js
import React, { useState } from 'react';
import "./style/auth.css";
const ForgotPassword = ({ switchToLogin, isLoading, handleAuthAction }) => {
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const [touched, setTouched] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const validateEmail = (value) => {
    if (!value) return 'Email is required';
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) return 'Email is invalid';
    return '';
  };

  const handleBlur = () => {
    setTouched(true);
    setError(validateEmail(email));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setTouched(true);
    
    const emailError = validateEmail(email);
    setError(emailError);
    
    if (!emailError) {
      await handleAuthAction(() => {
        // Firebase password reset logic will be implemented by backend developer
        console.log('Password reset requested for:', email);
        setIsSubmitted(true);
      });
    }
  };

  if (isSubmitted) {
    return (
      <div className="auth-form">
        <div className="success-state">
          <div className="success-icon">
            <svg viewBox="0 0 24 24" width="64" height="64">
              <path fill="#2ecc71" d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
            </svg>
          </div>
          <h2>Check Your Email</h2>
          <p>We've sent a password reset link to <strong>{email}</strong></p>
          <p className="instruction">Please check your inbox and follow the instructions to reset your password.</p>
          <button className="auth-button" onClick={switchToLogin}>
            Back to Login
          </button>
          <div className="resend-link">
            <p>Didn't receive the email? <span className="auth-link" onClick={handleSubmit}>Resend</span></p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="auth-form">
      <div className="form-header">
        <h2>Reset Your Password</h2>
        <p>Enter your email address and we'll send you a link to reset your password</p>
      </div>
      
      <form onSubmit={handleSubmit} noValidate>
        <div className="form-group">
          <label htmlFor="forgotEmail">Email Address</label>
          <input 
            type="email" 
            id="forgotEmail"
            placeholder="your.email@example.com" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            onBlur={handleBlur}
            className={error ? 'error' : ''}
            required 
          />
          {error && <span className="error-message">{error}</span>}
        </div>
        
        <button 
          type="submit" 
          className={`auth-button ${isLoading ? 'loading' : ''}`}
          disabled={isLoading}
        >
          {isLoading ? (
            <>
              <div className="spinner"></div>
              Sending Reset Link...
            </>
          ) : (
            'Send Reset Link'
          )}
        </button>
      </form>
      
      <div className="auth-footer">
        <p>Remember your password? <span className="auth-link" onClick={switchToLogin}>Back to Login</span></p>
      </div>
    </div>
  );
};

export default ForgotPassword;
