  };

  const clearFilters = () => {
    setFilters({
      brand: 'all',
      priceRange: 'all',
      sortBy: 'name-asc'
    });
  };

  return (
    <div className="product-list-container">
      <div className="filters-sidebar">
        <div className="filter-header">
