import React, { useState, useEffect } from 'react';
import './style/advertise.css';

const Advertise = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Move your images to public/advertise/ folder and use this approach
  const banners = [
    {
      id: 1,
      image: '/advertise/image1.png'
    },
    {
      id: 2,
      image: '/advertise/image2.png'
    },
    {
      id: 3,
      image: '/advertise/image3.png'
    },
    {
      id: 4,
      image: '/advertise/image4.png'
    },
    {
      id: 5,
      image: '/advertise/image5.png'
    }
  ];

  // Auto slide every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000);

    return () => clearInterval(interval);
  }, [currentSlide]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === banners.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? banners.length - 1 : prev - 1));
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <div className="banner-carousel">
      <div className="carousel-container">
        {/* Navigation Buttons */}
        <button className="nav-button prev-button" onClick={prevSlide}>
          <i className="fas fa-chevron-left"></i>
        </button>
        
        <button className="nav-button next-button" onClick={nextSlide}>
          <i className="fas fa-chevron-right"></i>
        </button>

        {/* Banner Slides */}
        <div className="slides-container">
          {banners.map((banner, index) => (
            <div
              key={banner.id}
              className={`slide ${index === currentSlide ? 'active' : ''}`}
            >
              <img 
                src={banner.image} 
                alt={`Banner ${banner.id}`}
                className="banner-image"
                onError={(e) => {
                  e.target.src = 'https://via.placeholder.com/1200x400/667eea/white?text=Banner+' + banner.id;
                }}
              />
            </div>
          ))}
        </div>

        {/* Indicators */}
        <div className="indicators">
          {banners.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Advertise;
