import React, { useState } from 'react';
import { useShop } from './ShopContext';
import '../style/productcard.css';

const ProductCard = ({ product, onAddToCart, onAddToWishlist }) => {
  const [isWishlisted, setIsWishlisted] = useState(false);
    const { addToCart, toggleWishlist, isInWishlist } = useShop();

    const handleAddToCart = () => {
    addToCart(product);
     onAddToCart(product.id);
  };

  const handleWishlist = () => {
    toggleWishlist(product);
  };

  const handleWishlistClick = () => {
    setIsWishlisted(!isWishlisted);
    onAddToWishlist(product.id, !isWishlisted);
  };

  // const handleAddToCart = () => {
  //   onAddToCart(product.id);
  // };

  const discount = product.specialPrice 
    ? Math.round(((product.price - product.specialPrice) / product.price) * 100)
    : 0;

  return (
    <div className="product-card">
      <div className="product-image-container">
        <img 
          src={product.image} 
          alt={product.name}
          className="product-image"
          onError={(e) => {
            e.target.src = `https://t3.ftcdn.net/jpg/00/34/70/32/360_F_34703220_TiczZRk73LnvUcvt2J2qj57mKzwKAtBT.jpg=${product.name}`;
          }}
        />
        {discount > 0 && (
          <span className="discount-badge">-{discount}%</span>
        )}
        <button 
          className={`wishlist-btn ${isWishlisted ? 'wishlisted' : ''}`}
          onClick={handleWishlistClick}
        >
          <i className={`fas ${isWishlisted ? 'fa-heart' : 'fa-heart'}`}></i>
        </button>
      </div>

      <div className="product-info">
        <span className="product-brand">{product.brand}</span>
        <h3 className="product-name">{product.name}</h3>
        
        <div className="product-pricing">
          {product.specialPrice ? (
            <>
              <span className="special-price">PKR {product.specialPrice.toLocaleString()}</span>
              <span className="original-price">PKR {product.price.toLocaleString()}</span>
            </>
          ) : (
            <span className="price">PKR {product.price.toLocaleString()}</span>
          )}
        </div>

        <button className="add-to-cart-btn" onClick={handleAddToCart}>
          <i className="fas fa-shopping-cart"></i>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
